import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const HeroCarousel = () => {
  const images = [
    "carousel",
    "https://picsum.photos/id/1015/1920/1080",
    "https://picsum.photos/id/1019/1920/1080",
    "https://picsum.photos/id/1022/1920/1080",
  ];

  const settings = {
    dots: false,         // no dots
    infinite: true,
    speed: 1000,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 4000,
    arrows: true,        // show arrows
  };

  return (
    <div className="relative w-full h-screen overflow-hidden">
      <Slider {...settings} className="w-full h-full">
        {images.map((img, idx) => (
          <div key={idx} className="w-full h-screen">
            <div
              className="w-full h-screen bg-cover bg-center"
              style={{ backgroundImage: `url(${img})` }}
            >
              {/* Overlay */}
              <div className="absolute inset-0 bg-black bg-opacity-40 flex flex-col items-center justify-center text-center text-white px-4">
                <h1 className="text-5xl md:text-6xl font-bold mb-4">
                  Adventure is worthwhile
                </h1>
                <p className="text-xl md:text-2xl mb-6">
                  Discover new places with us, Adventure awaits
                </p>
                <button className="bg-orange-500 hover:bg-orange-600 text-white px-6 py-3 rounded-lg font-semibold transition transform hover:scale-105">
                  Discover More
                </button>
              </div>
            </div>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default HeroCarousel;
