import React, { useState } from "react";
import { useSelector } from "react-redux";
import { useParams, useNavigate } from "react-router-dom";

const DestinationDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const destinations = useSelector((state) => state.destinations);
  const destination = destinations.find((d) => d.id === parseInt(id));

  // Fake authentication check (replace with real auth later)
  const isLoggedIn = false;

  const [date, setDate] = useState("");
  const [persons, setPersons] = useState(1);
  const [confirmed, setConfirmed] = useState(false);

  const handleBooking = () => {
    if (!isLoggedIn) {
      navigate("/login");
    } else {
      setConfirmed(true);
    }
  };

  if (!destination) return <h2 className="pt-24 text-center">Destination not found</h2>;

  return (
    <div className="pt-24 px-6 md:px-12 lg:px-20 flex flex-col md:flex-row gap-10">
      {/* Image + Details */}
      <div className="flex-1">
        <img
          src={destination.image}
          alt={destination.name}
          className="w-full h-80 object-cover rounded-xl shadow-lg"
        />
        <h2 className="text-3xl font-bold mt-4">{destination.name}</h2>
        <p className="text-gray-500">{destination.location}</p>
        <p className="text-lg mt-3">{destination.description}</p>
      </div>

      {/* Booking Form */}
      <div className="flex-1 bg-white rounded-xl shadow-lg p-6">
        <h3 className="text-2xl font-semibold mb-4">Book Your Trip</h3>

        {confirmed ? (
          <p className="text-green-600 font-bold text-xl">ðŸŽ‰ Order Confirmed!</p>
        ) : (
          <>
            <label className="block mb-2 font-medium">Select Date</label>
            <input
              type="date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              className="border rounded-lg px-3 py-2 w-full mb-4"
            />

            <label className="block mb-2 font-medium">Number of Persons</label>
            <input
              type="number"
              value={persons}
              onChange={(e) => setPersons(e.target.value)}
              className="border rounded-lg px-3 py-2 w-full mb-4"
              min="1"
            />

            <button
              onClick={handleBooking}
              className="w-full bg-orange-500 hover:bg-orange-600 text-white py-2 rounded-lg font-semibold transition"
            >
              Book Now
            </button>
          </>
        )}
      </div>
    </div>
  );
};

export default DestinationDetails;
