// src/components/Carousel.jsx
import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Carousel = () => {
  const images = [
    ,
    "https://picsum.photos/id/1015/1600/700",
    "https://picsum.photos/id/1019/1600/700",
    "https://picsum.photos/id/1022/1600/700",
  ];

  const settings = {
    dots: false, // remove dots
    infinite: true,
    speed: 1000,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 4000,
    arrows: true, // show arrows
  };

  return (
    <div className="relative w-full h-screen">
      <Slider {...settings} className="h-full">
        {images.map((img, idx) => (
          <div key={idx}>
            <div
              className="w-full h-screen bg-cover bg-center"
              style={{ backgroundImage: `url(${img})` }}
            >
              <div className="flex flex-col justify-center items-center h-full bg-black bg-opacity-50 text-white text-center">
                <h1 className="text-5xl font-bold mb-4">Adventure is worthwhile</h1>
                <p className="text-xl mb-6">
                  Discover new places with us, Adventure awaits
                </p>
                <button className="bg-orange-500 hover:bg-orange-600 text-white px-6 py-3 rounded-lg transition transform hover:scale-105">
                  Discover More
                </button>
              </div>
            </div>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default Carousel;
