import React from "react";
import { useSelector } from "react-redux";

const Destinations = () => {
  const destinations = useSelector((state) => state.destinations);

  return (
    <div className="pt-24 px-6 md:px-12 lg:px-20 bg-gray-50 min-h-screen">
      <h2 className="text-4xl md:text-5xl font-bold text-center text-blue-600 mb-12">
        Explore Our Destinations
      </h2>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
        {destinations.map((place) => (
          <div
            key={place.id}
            className="bg-white rounded-2xl shadow-lg overflow-hidden transform transition duration-300 hover:scale-105 hover:shadow-2xl"
          >
            <img
              src={place.image}
              alt={place.name}
              className="w-full h-56 object-cover"
            />
            <div className="p-5">
              <h3 className="text-xl font-bold text-gray-800">{place.name}</h3>
              <p className="text-gray-500">{place.location}</p>
              <p className="text-lg font-semibold text-orange-500 mt-2">
                ${place.price} / person
              </p>
              <button className="mt-4 w-full bg-orange-500 hover:bg-orange-600 text-white py-2 rounded-lg font-semibold transition transform hover:scale-105">
                Book Now
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Destinations;
