import React from "react";
import Slider from "react-slick";

const HeroCarousel = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 800,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
    arrows: false,
  };

  const images = [
    "carousel.jpg",
    "/images/slide2.jpg",
    "/images/slide3.jpg",
    "/images/slide4.jpg",
  ];

  return (
    <div className="relative mt-16"> {/* mt-16 so it sits below navbar */}
      <Slider {...settings}>
        {images.map((img, index) => (
          <div key={index} className="relative h-[90vh]">
            <img
              src={img}
              alt={`slide-${index}`}
              className="w-full h-full object-cover"
            />
            {/* Overlay Text */}
            <div className="absolute inset-0 bg-black bg-opacity-40 flex flex-col items-center justify-center text-center text-white px-4">
              <h1 className="text-4xl md:text-6xl font-bold mb-4 animate-fade-in">
                Adventure is worthwhile
              </h1>
              <p className="text-lg md:text-2xl mb-6">
                Discover new places with us, Adventure awaits
              </p>
              <button className="bg-orange-500 hover:bg-orange-600 text-white px-6 py-3 rounded-lg font-semibold transition transform hover:scale-105">
                Discover More
              </button>
            </div>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default HeroCarousel;
