import React from "react";

const Services = () => {
  const services = [
    {
      title: "Travel Booking",
      description: "Book flights, trains, and buses with ease and flexibility.",
      icon: "‚úàÔ∏è",
    },
    {
      title: "Hotel Reservations",
      description: "Find and reserve hotels that fit your budget and style.",
      icon: "üè®",
    },
    {
      title: "Tour Packages",
      description: "Explore curated travel packages with guided tours.",
      icon: "üåç",
    },
    {
      title: "Customer Support",
      description: "24/7 customer support for a hassle-free travel experience.",
      icon: "üìû",
    },
  ];

  return (
    <div className="p-8 bg-gray-50 min-h-screen">
      <h1 className="text-3xl font-bold text-center mb-8">Our Services</h1>
      <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-6">
        {services.map((service, index) => (
          <div
            key={index}
            className="bg-white rounded-2xl shadow-md p-6 text-center hover:shadow-xl transition"
          >
            <div className="text-4xl mb-4">{service.icon}</div>
            <h2 className="text-xl font-semibold mb-2">{service.title}</h2>
            <p className="text-gray-600">{service.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Services;
